sys_script_include.script:
var CreateRecords = Class.create();
CreateRecords.prototype = {
    initialize: function() {
var needGR = new GlideRecord('x_58872_needit_needit');



var userArray = [];
var ruquestType = ['Human Resources', 'Legal', 'Facilities'];
var userGR = new GlideRecord('sys_user');


userGR.addQuery('active', true);
userGR.setLimit(8);
userGR.query();

while(userGR.next()){
  userArray.push(userGR.getUniqueValue());
}



//gs.info(userArray[random]);

//gs.info(random);

//gs.info(ruquestType[reqRandom]);

//gs.info(ruquestType[reqRandom]+' '+1);

//gs.info(gs.beginningOfNextMonth());


for (var i = 0; i< 20; i++){
var random = Math.floor(Math.random() * 8);
var reqRandom = Math.floor(Math.random() * 3);
needGR.newRecord();

needGR.u_requested_for = userArray[random];
needGR.u_request_type = ruquestType[reqRandom];
needGR.state = 'Requested';
needGR.u_what_needed = ruquestType[reqRandom] +' '+1;
needGR.u_when_needed = gs.beginningOfNextMonth();
needGR.u_requested_for_email = userArray[random] + '@123.com';

needGR.insert();
}
    },

    type: 'CreateRecords'
};
